import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { Pagination } from "./components/Pagination";
import thumbnail from './assets/img/thumbnail.jpeg'

function ListPosts() {

  document.title = "List Posts Page";

  const [posts, setPosts] = useState([])
  const [currentPage, setCurrentPage] = useState(1)
  const [postsPerPage] = useState(5)
  const postsApi = "http://127.0.0.1:8000/api/books";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage //10
  const indexOfFirstPost = indexOfLastPost - postsPerPage // 0
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost)

  return (
    <div>
      <h2>List Posts</h2>
      <div className="container">
        <div className="row m-2">
          {posts.map((post) => (

            <div className="card" style={{ width: "18rem" }} key={post.id}>
              <img className="card-img-top" src={thumbnail} alt=""/>
              <div className="card-body">
                <h5 className="card-title">{post.title} </h5>
                <p className="card-text">
                  {post.author} - {post.publisher}
                </p>
                <button className="btn btn-primary">
                  <NavLink style={{color: "#fff", textDecoration: "none"}} to={`/posts/${post.id}`}>Read More</NavLink>
                </button>
              </div>
            </div>
          ))}
          <Pagination postsPerPage={postsPerPage} totalPosts={posts.length} paginate={paginate}
     />
        </div>
      </div>
    </div>
  );
}

export default ListPosts;
