import { useState, useEffect } from "react";
import { useParams } from 'react-router-dom'

function Post() {
  const {id} = useParams()
  const [post, setPost] = useState([]);

  const postsApi = "http://127.0.0.1:8000/api/books";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  return (
    <div>
      <h1>Post Page</h1>
      <p>{id}</p>
    </div>
  )
}

export default Post
