import { useState, useEffect } from 'react'

function Posts() {

  const [posts, setPosts] = useState([])
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [publisher, setPublisher] = useState('')

  const postsApi = 'http://127.0.0.1:8000/api/books'
  const putPostApi = 'http://127.0.0.1:8000/api/book'

  useEffect(() => {
    fetch(postsApi)
      .then(response => response.json())
      .then(posts => setPosts(posts))
  },[])

  const putPost = () => {
    var options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: 'test title',
        author: 'test author',
        publisher: 'test publisher'
      })
    };

    fetch(putPostApi, options)
      .then(response => response.json())
      .then(console.log('OK'));
  }


  console.log(posts)

  return (
    <div>
      <h2>Test Posts</h2>
      <div>
        <label>Title : <input type="text" placeholder='Enter title' onChange={e => setTitle(e)}/></label>
        <label>Author : <input type="text" placeholder='Enter author'/></label>
        <label>Publisher : <input type="text" placeholder='Enter publisher'/></label>
      </div>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>{post.title} - {post.author} - {post.publisher}</li>
        ))
        }
      </ul>
      <button onClick={putPost}>Submit</button>
    </div>
  )
}

export default Posts
