import { useState, useRef, useEffect } from "react";

export function CreatePost() {

  // Cập nhật title, author, description 
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [date, setDate] = useState(null);
  const [description, setPublisher] = useState("");

  const titleRef = useRef();

  const putPostApi = "http://127.0.0.1:8000/api/book";

  // Cập nhật title
  useEffect(() => {
    document.title = "Create Post Page";
  },);

  /**
   * ! Gửi thông tin bài viết người dùng tạo lên Database
   */
  const publicPost = () => {
    var options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        description,
      }),
    };

    fetch(putPostApi, options)
      .then((response) => response.json())
      .then(alert('Bài viết của bạn đã được công bố'))

    // Reset thông tin trên các ô input
    setTitle("");
    setAuthor("");
    setPublisher("");
    // Sau khi người dùng nhấn nút Public và gửi thông tin bài viết đi thì input Title sẽ được focus để người dùng tiếp tục nhập
    titleRef.current.focus();
  };

  return (
    <div style={{paddingTop: 80, paddingBottom: 80}}>
      <h2>Create Posts</h2>
      <div className="mt-3">

        {/* Form nhập thông tin bài viết */}
        <form>
          <div className="form-group">
            <label>Post Title</label>
            <input
              className="form-control"
              type="text"
              value={title}
              placeholder="Enter title"
              onChange={(e) => setTitle(e.target.value)}
              ref={titleRef}
            />
          </div>
          <br />
          <div className="form-group">
            <label>Author</label>
            <input
              className="form-control"
              type="text"
              value={author}
              placeholder="Enter author"
              onChange={(e) => setAuthor(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Date</label>
            <input
              className="form-control"
              type="date"
              placeholder="Enter date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </div>
          <br />
          <div className="form-group">
            <label>Description</label>
            <input
              className="form-control"
              type="text"
              value={description}
              placeholder="Enter description"
              onChange={(e) => setPublisher(e.target.value)}
            />
          </div>
          <br />
          <button type="button" className="btn btn-primary" onClick={publicPost}>
            Publish
          </button>
        </form>

        <br />

        {/* Hiển thị thông tin người dùng nhập dưới dạng bài viết mẫu */}
        <div>
          <h3>
            {title || "Title"} - {author || "Author"} -{" "}
            {description || "description"}
          </h3>
        </div>
      </div>

    </div>
  );
}
