import { useState, useEffect, useRef } from 'react'

function Posts() {

  const [posts, setPosts] = useState([])
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [publisher, setPublisher] = useState('')

  const titleRef = useRef()

  const postsApi = 'http://127.0.0.1:8000/api/books'
  const putPostApi = 'http://127.0.0.1:8000/api/book'

  useEffect(() => {
    fetch(postsApi)
      .then(response => response.json())
      .then(posts => setPosts(posts))
  },[])

  const putPost = () => {
    var options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher
      })
    };
    fetch(putPostApi, options)
      .then(response => response.json())
      .then(console.log('OK'));

      titleRef.current.focus()
  }




  const deletePost = (id) => {
    var options = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      }
    };
    fetch(putPostApi +  '/' + id, options)
      .then(response => response.json())
      .then(console.log('OK'));
  }

  const getPost = (id) => {
    var options = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      }
    };
    fetch(putPostApi +  '/' + id, options)
      .then(response => response.json())
      .then(post => { return (
        setTitle(post.title),
        setAuthor(post.author),
        setPublisher(post.publisher))
      });
  }

  const editPost = (id) => {
    var options = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher
      })
    };
    fetch(putPostApi +  '/' + id, options)
      .then(response => response.json())
      .then(console.log('OK'));

    setTitle('')
    setAuthor('')
    setPublisher('')
  }

  console.log(title, author, publisher)

  return (
    <div>
      <h2>Test Posts</h2>
      <div>
        <label>Title : <input 
          type="text" 
          value={title} 
          placeholder='Enter title' 
          onChange={e => setTitle(e.target.value)}
          ref={titleRef}
          /></label>
        <label>Author : <input type="text" value={author} placeholder='Enter author' onChange={e => setAuthor(e.target.value)}/></label>
        <label>Publisher : <input type="text" value={publisher} placeholder='Enter publisher' onChange={e => setPublisher(e.target.value)}/></label>
      </div>
      <div>
        <p>{title || 'Title'} - {author || 'Author'} - {publisher || 'Publisher'}</p>
      </div>
      <ul>
        {posts.map((post) => (
          <div key={post.id}>
            <li>{post.id} - {post.title} - {post.author} - {post.publisher}</li>
            <button onClick={() => deletePost(post.id)}>Delete</button>
            <button onClick={() => getPost(post.id)}>Edit</button>
            <button onClick={() => editPost(post.id)}>Submit Edit</button>
            <br />
            <br />
          </div>
        ))
        }
      </ul>
      <button onClick={putPost}>Submit</button>

    </div>
  )
}

export default Posts
