import { useEffect } from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import login from "../assets/img/login.webp";

const schema = yup.object({
  username
})

export function Login() {
  // Cập nhật title
  useEffect(() => {
    document.title = "Login Page";
  });

  const { handleSubmit, register } = useForm({
    resolever: yupResolver(schema)
  });

  const formSubmit = (data) => {
    console.log(data);
  };

  return (
    <section className="d-flex align-items-center justify-content-center vh-100">
      <div className="container-fluid h-custom">
        <div className="row d-flex justify-content-center align-items-center h-100">

          <div className="col-md-9 col-lg-6 col-xl-5 mb-5">
            <img src={login} className="img-fluid" alt="Login" />
          </div>

          <div className="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
            <h2 className="text-center mb-3">Login Form</h2>
            <p>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Tempora
              aspernatur saepe magnam culpa ipsum molestiae laborum quod earum
              quam dignissimos?
            </p>
            <form onSubmit={handleSubmit(formSubmit)}>
              {/* <!-- Email input --> */}
              <div className="form-outline mb-3">
                <label className="form-label">Email address</label>
                <input
                  type="email"
                  className="form-control"
                  placeholder="Enter a valid email address"
                  {...register("username")}
                />
              </div>
              {/* <!-- Password input --> */}
              <div className="form-outline mb-3">
                <label className="form-label">Password</label>
                <input
                  type="password"
                  className="form-control"
                  placeholder="Enter password"
                  {...register("password")}
                />
                <div className="text-center text-lg-start mt-4 pt-2">
                  <button type="submit" className="btn btn-primary btn-sm">
                    Login to Admin Page
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}
