import { useParams } from 'react-router-dom'

function Post() {
  const {id} = useParams()
  const [post, setPost] = useState([]);

  const postApi = `http://127.0.0.1:8000/api/book/${id}`;

  console.log(postApi);

  useEffect(() => {
    fetch(postApi)
      .then((response) => response.json())
      .then((post) => setPost(post));
  }, []);

  console.log(post)

  return (
    <div className="container">
        <div className="row">

        </div>
      </div>
  )
}

export default Post
