import { useState, useEffect, useRef } from "react";

function EditPost() {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [publisher, setPublisher] = useState("");
  const [show, setShow] = useState(false);

  const postsApi = "http://127.0.0.1:8000/api/books";
  const putPostApi = "http://127.0.0.1:8000/api/book";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  const deletePost = (id) => {
    var options = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then(console.log("OK"));
  };

  const getPost = (id) => {
    var options = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    };

    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then((post) => {
        return (
          setTitle(post.title),
          setAuthor(post.author),
          setPublisher(post.publisher)
        );
      });

    setShow(true);
  };

  const editPost = (id) => {
    var options = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher,
      }),
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then(console.log("OK"));

    setTitle("");
    setAuthor("");
    setPublisher("");
  };

  return (
    <div>
      <h2>Edit Posts</h2>
      {show && (
        <div>
          <form>
            <div class="form-group">
              <label>Post Title</label>
              <input
                className="form-control"
                type="text"
                value={title}
                placeholder="Enter title"
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <br />
            <div class="form-group">
              <label>Post Author</label>
              <input
                className="form-control"
                type="text"
                value={author}
                placeholder="Enter author"
                onChange={(e) => setAuthor(e.target.value)}
              />
            </div>
            <br />
            <div class="form-group">
              <label>Post Publisher</label>
              <input
                className="form-control"
                type="text"
                value={publisher}
                placeholder="Enter publisher"
                onChange={(e) => setPublisher(e.target.value)}
              />
            </div>
            <br />
            <button type="button" className="btn btn-primary">
              Publish
            </button>
          </form>
        </div>
      )}

      <table class="table table-hover">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">First</th>
            <th scope="col">Last</th>
            <th scope="col">Handle</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <th scope="row">3</th>
            <td colspan="2">Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </table>

      <ul>
        {posts.map((post) => (
          <div key={post.id}>
            <li>
              {post.id} - {post.title} - {post.author} - {post.publisher}
            </li>
            <button
              style={{ marginRight: 10 }}
              type="button"
              className="btn btn-danger"
              onClick={() => deletePost(post.id)}
            >
              Delete
            </button>
            <button
              style={{ marginRight: 10 }}
              type="button"
              className="btn btn btn-warning"
              onClick={() => getPost(post.id)}
            >
              Edit
            </button>
            {show && (
              <button
                type="button"
                className="btn btn-info"
                onClick={() => editPost(post.id)}
              >
                Submit Edit
              </button>
            )}
            <br />
            <br />
          </div>
        ))}
      </ul>
    </div>
  );
}

export default EditPost;
