import { useState, useEffect } from 'react'

function Posts() {

  const [posts, setPosts] = useState([])

  const postsApi = 'http://127.0.0.1:8000/api/books'
  const putPostApi = 'http://127.0.0.1:8000/api/book'

  useEffect(() => {
    fetch(postsApi)
      .then(response => response.json())
      .then(posts => setPosts(posts))
  },[])

  const data = {
    title: 'test post',
    author: 'test author',
    publisher: 'test publisher'
  }

  const putPost = (data) => {
    console.log(data);
    var options = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data)
    };
    fetch(putPostApi, options)
      .then(function (response) {
        response.json();
      })
      .then(posts => setPosts(posts));
  }


  console.log(posts)

  return (
    <div>
      <h2>Test Posts</h2>
      <ul>
        {posts.map((post) => (
          <li key={post.id}>{post.title} - {post.author} - {post.publisher}</li>
        ))
        }
      </ul>
      <button onClick={putPost}>Submit</button>
    </div>
  )
}

export default Posts
