import { useState, useEffect } from "react";
import thumbnail from './assets/img/thumbnail.jpeg'
function ListPosts() {
  document.title = "List Posts Page";

  const [posts, setPosts] = useState([]);

  const postsApi = "http://127.0.0.1:8000/api/books";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  return (
    <div>
      <h2>List Posts</h2>
      <div className="container">
        <div className="row">
          {posts.map((post) => (
            <div className="card" style={{ width: "18rem" }} key={post.id}>
              <img className="card-img-top" src={thumbnail} alt="Card image cap"/>
              <div className="card-body">
                <h5 className="card-title">{post.title} </h5>
                <p className="card-text">
                  {post.author} - {post.publisher}
                </p>
                <a href="#" className="btn btn-primary">
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ListPosts;
