import { useState, useEffect, useRef } from "react";

function EditPost() {
  document.title = "Edit Post Page";

  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [publisher, setPublisher] = useState("");
  const [show, setShow] = useState(false);

  const postsApi = "http://127.0.0.1:8000/api/books";
  const putPostApi = "http://127.0.0.1:8000/api/book";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  const deletePost = (id) => {

    if (window.confirm("Are you sure you want to delete this post?") == true) {

    }


  };

  const editPost = (id) => {
    var options = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher,
      }),
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then(console.log("OK"));

    setTitle("");
    setAuthor("");
    setPublisher("");
  };

  return (
    <div style={{marginTop: 25}}>
      <h2>Edit Posts</h2>

      {show && (
        <div>
          <form>
            <div class="form-group">
              <label>Post Title</label>
              <input
                className="form-control"
                type="text"
                value={title}
                placeholder="Enter title"
                onChange={(e) => setTitle(e.target.value)}
              />
            </div>
            <br />
            <div class="form-group">
              <label>Post Author</label>
              <input
                className="form-control"
                type="text"
                value={author}
                placeholder="Enter author"
                onChange={(e) => setAuthor(e.target.value)}
              />
            </div>
            <br />
            <div class="form-group">
              <label>Post Publisher</label>
              <input
                className="form-control"
                type="text"
                value={publisher}
                placeholder="Enter publisher"
                onChange={(e) => setPublisher(e.target.value)}
              />
            </div>
          </form>
          <br />
        </div>
      )}

      <table class="table table-hover">
        <thead>
          <tr className="table-primary">
            <th scope="col">No</th>
            <th scope="col">Title</th>
            <th scope="col">Author</th>
            <th scope="col">Publisher</th>
            <th scope="col">Button</th>
          </tr>
        </thead>

        <tbody>
          {posts.map((post, index) => (
            <tr key={post.id}>
              <th scope="row">{index + 1}</th>
              <td>{post.title}</td>
              <td>{post.author}</td>
              <td>{post.publisher}</td>
              <td>
                <button
                  style={{ marginRight: 10 }}
                  type="button"
                  className="btn btn-danger"
                  onClick={() => deletePost(post.id)}
                >
                  Delete
                </button>

                <button
                  style={{ marginRight: 10 }}
                  type="button"
                  className="btn btn btn-warning"
                  onClick={() => getPost(post.id)}
                >
                  Edit
                </button>
                {show && (
                  <button
                    type="button"
                    className="btn btn-info"
                    onClick={() => editPost(post.id)}
                  >
                    Submit Edit
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EditPost;
