import { useState, useEffect, useRef } from "react";

function EditPost() {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [publisher, setPublisher] = useState("");

  const titleRef = useRef();

  const postsApi = "http://127.0.0.1:8000/api/books";
  const putPostApi = "http://127.0.0.1:8000/api/book";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  const deletePost = (id) => {
    var options = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then(console.log("OK"));
  };

  const getPost = (id) => {
    var options = {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then((post) => {
        return (
          setTitle(post.title),
          setAuthor(post.author),
          setPublisher(post.publisher)
        );
      });
  };

  const editPost = (id) => {
    var options = {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher,
      }),
    };
    fetch(putPostApi + "/" + id, options)
      .then((response) => response.json())
      .then(console.log("OK"));

    setTitle("");
    setAuthor("");
    setPublisher("");

    titleRef.current.focus();
  };

  return (
    <div>
      <h2>Edit Posts</h2>

      <div>
        <form>
          <div class="form-group">
            <label>Post Title</label>
            <input
              className="form-control"
              type="text"
              value={title}
              placeholder="Enter title"
              onChange={(e) => setTitle(e.target.value)}
              ref={titleRef}
            />
            <small class="form-text text-muted">
              We'll never share your email with anyone else.
            </small>
          </div>
          <br />
          <div class="form-group">
            <label>Post Author</label>
            <input
              className="form-control"
              type="text"
              value={author}
              placeholder="Enter author"
              onChange={(e) => setAuthor(e.target.value)}
            />
          </div>
          <br />
          <div class="form-group">
            <label>Post Publisher</label>
            <input
              className="form-control"
              type="text"
              value={publisher}
              placeholder="Enter publisher"
              onChange={(e) => setPublisher(e.target.value)}
            />
          </div>
          <br />
          <button type="button" className="btn btn-primary" onClick={putPost}>
            Publish
          </button>
        </form>
      </div>

      <ul>
        {posts.map((post) => (
          <div key={post.id}>
            <li>
              {post.id} - {post.title} - {post.author} - {post.publisher}
            </li>
            <button type="button" className="btn btn-danger" onClick={() => deletePost(post.id)}>Delete</button>
            <button type="button" className="btn btn btn-warning" onClick={() => getPost(post.id)}>Edit</button>
            <button type="button" className="btn btn-info" onClick={() => editPost(post.id)}>Submit Edit</button>
            <br />
            <br />
          </div>
        ))}
      </ul>
    </div>
  );
}

export default EditPost;
