import { useState, useEffect, useRef } from "react";

function CreatePost() {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [publisher, setPublisher] = useState("");

  const titleRef = useRef();
  const authorRef = useRef();
  const publisherRef = useRef();

  const postsApi = "http://127.0.0.1:8000/api/books";
  const putPostApi = "http://127.0.0.1:8000/api/book";

  useEffect(() => {
    fetch(postsApi)
      .then((response) => response.json())
      .then((posts) => setPosts(posts));
  }, []);

  const putPost = () => {
    var options = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title,
        author,
        publisher,
      }),
    };
    fetch(putPostApi, options)
      .then((response) => response.json())
      .then(console.log("OK"));

    setTitle("");
    setAuthor("");
    setPublisher("");

    titleRef.current.focus();
    authorRef.current.focus();
    publisherRef.current.focus();
  };

  return (
    <div>
      <h2>Create Posts</h2>
      <div>
        <label>
          Title :{" "}
          <input
            type="text"
            value={title}
            placeholder="Enter title"
            onChange={(e) => setTitle(e.target.value)}
            ref={titleRef}
          />
        </label>
        <label>
          Author :{" "}
          <input
            type="text"
            value={author}
            placeholder="Enter author"
            onChange={(e) => setAuthor(e.target.value)}
            ref={authorRef}
          />
        </label>
        <label>
          Publisher :{" "}
          <input
            type="text"
            value={publisher}
            placeholder="Enter publisher"
            onChange={(e) => setPublisher(e.target.value)}
            ref={publisherRef}
          />
        </label>
      </div>
      <br />
      <div>
        <p>
          {title || "Title"} - {author || "Author"} - {publisher || "Publisher"}
        </p>
      </div>
      <button type="button" classname="btn btn-primary" onClick={putPost}>Publish</button>
    </div>
  );
}

export default CreatePost;
